graphql.query:
mutation ($locationName: String!, $characterName: String!, $episodeName: String!) 
{
   createLocation(location: {name: $locationName, type: "SouthZoneTwo", dimension: "235g"}) 
  {
    id
  }  
  createCharacter(character: {name: $characterName, type: "Machoo", status: "Alive", species: "fantasy", gender: "Male", image: "png", originId: 567, locationId: 5589}) 
  {
    id
  }
  
    createEpisode(episode: {name: $episodeName, air_date: "28July2024", episode: "ID123438"})
  {
    id
  }

}

query($characterId:Int!)
{
  character(characterId: $characterId) {
    id
    name
    type
    status
    species
    gender
  }
  location(locationId: 11959) {
    id
    type
    dimension
    created
  }
  episode(episodeId: 9284) {
    id
    air_date
    name
  }
    # character type argument is not scaler so we need to add 
  #curly braces, same concept for info
  # e.g. location has int argument which is scalar 
  characters(filters: {name: "RobinAkshay"}) {
    info {
      count
    }
    result {
      status
      species
    }
  }
  
  episodes(filters:{episode:"ID123"})
  {
    result
    {
      id
      name
      air_date
      episode
    }
  }
}
